import React from 'react';
import './App.css';
import image from "./images/headshot.jpg";
import Buttons from "./Components/Buttons.js";
import { Redirect } from 'react-router-dom'
import Routing from "./Components/Routes.js";
import { Navigation } from 'react-mdl';
import { Link } from 'react-router-dom';
import './Components/About.js';


export class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            //figure this out
            redirect: false
        }
    }

    setRedirect = () => {
        this.setState({
            redirect: true
        })
    }

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to='/target' />
        }
    }



  render() {
      return (
    <div className="App">
      <header className="App-header">
        <p className="Title">Melanie Rogoff</p>
        <img src={image} className="headshot" alt="myphoto" />
            <Buttons onClick={this.setRedirect}>Redirect</Buttons>
      </header>

      <Navigation>
          <Link to="../Components/Resume.js">Resume</Link>
          <Link to="../Components/About.js">About Me</Link>
          <Link to="../Components/Portfolio.js">Portfolio</Link>
          <Link to="../Components/Contact.js">Contact</Link>
      </Navigation>
      <Routing/>
    </div>

  );
}
}



export default App;
